import app from "./app";

import { LogClient } from "./clients/logger";
import { SERVER_PORT, NodeEnv, APP_NAME_DEFAULT } from "./constants/server";
import { MySQLClient } from "./clients/mysql";

const PORT = process.env.SERVER_PORT || SERVER_PORT;

